import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Average extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            fuel: "0",
            km: "0",
            cost: "0",

            fuelC: "0",
            kmC: "0",
            costC: "0"
        };
        this.changeStateFuel = this.changeStateFuel.bind(this)
        this.changeStateKm = this.changeStateKm.bind(this)
        this.changeStateCost = this.changeStateCost.bind(this)

        this.cChangeStateFuel = this.changeStateFuel.bind(this)
        this.cChangeStateKm = this.changeStateKm.bind(this)
        this.cChangeStateCost = this.changeStateCost.bind(this)
    }

    changeStateFuel(event) {
        this.setState({
            fuel: event.target.value
        })
      };
    changeStateKm(event) {
        this.setState({
            km: event.target.value
        })
    };
    changeStateCost(event) {
        this.setState({
            cost: event.target.value
        })
    }

    cChangeStateFuel(event) {
        this.setState({
            fuel: event.target.value
        })
      };
    cChangeStateKm(event) {
        this.setState({
            km: event.target.value
        })
    };
    cChangeStateCost(event) {
        this.setState({
            cost: event.target.value
        })
    }

    render(){
    return(
        <div>

        </div>
        );
    }
}

class Cost extends React.Component{
    render(){
    return(
        <div>
            <h1>Kalkulator kosztu przejechania trasy</h1>
            <p>Długość planowanej trasy w kilometrach: <input type="number" fuelC={this.state.value}
            onChange={this.cChangeStateFuel}/></p>
            <p>Średnie spalanie w litrach na 100 km: <input type="number" kmC={this.state.value}
            onChange={this.cChangeStateKm}/></p>
            <p>Cena litra paliwa: <input type="number" costC={this.state.value}
            onChange={this.cChangeStateCost}/></p>
            <button>Oblicz</button>
            <Average />
        </div>
        ); 
    }
}


ReactDOM.render(<Cost />, document.getElementById('root'));
